mapper.select:
select *
from (
		select @rownum:=@rownum+1 rownum, board.* 
		from board, (select @rownum:=0) as tmp
		where b_type='notice'
		
			and (b_title like concat('%',#{keyword},'%')
				or b_content like concat('%',#{keyword},'%'))
		
		
        order by b_no desc
) as b 

where rownum > (#{pageNum}-1)*#{amount} and rownum <= #{pageNum}*#{amount}

select *
from (
		select @rownum:=@rownum+1 rownum, board.* 
		from board, (select @rownum:=0) as tmp
		where b_type='faq'
		
			and (b_title like concat('%',#{keyword},'%')
				or b_content like concat('%',#{keyword},'%'))
						
        order by b_no desc
) as b 

where rownum > (#{pageNum}-1)*#{amount} and rownum <= #{pageNum}*#{amount}
